SELECT c.thing_code,c.metric_code,m.metric_type2_code as metricType,
s.system_id,t.thing_type1_code as assetType,t.thing_type2_code as category,m.metric_name,t.thing_name FROM tb_alert_param_configuration_list c LEFT JOIN tb_metric m ON c.metric_code = m.metric_code LEFT JOIN tb_thing t on c.thing_code = t.thing_code LEFT JOIN rel_thing_system s ON c.thing_code = s.thing_code
c.alert_type = #{filter.alertType}
AND t.thing_type1_code = #{filter.assetType}
AND t.thing_type2_code = #{filter.category}
AND s.system_id = #{filter.system}
AND m.metric_type2_code = #{filter.metricType}
AND c.metric_code= #{filter.metricCode}
AND c.thing_code = #{filter.thingCode}
ORDER BY thing_code ASC,metric_name ASC
order BY metric_name ASC ,category asc,thing_code asc
ORDER BY category ASC,metric_name asc,thing_code asc
limit #{filter.offset},#{filter.count}

