        AND
        m.metric_type2_code = #{filter.metricType}


        AND
        c.metric_code= #{filter.metricCode}


        AND
        c.thing_code = #{filter.thingCode}

SELECT id, thing_code, metric_code, rule_type, alert_level, lower_limit, upper_limit, enable, delay_time FROM tb_alert_rule WHERE thing_code = #{thingCode} AND metric_code = #{metricCode}
AND alert_level = #{filter.level}
AND enable = TRUE

SELECT ad.id,ad.thing_code,ad.metric_code,ad.alert_source,ad.alert_type,ad.alert_level,ad.alert_datetime,ad.alert_info,ad.reporter, ad.alert_stage,ad.is_repair,ad.repair_confirm_user,ad.repair_start_time,ad.repair_end_time, ad.is_manual_intervention,ad.feedback_image,ad.feedback_video,ad.scene_confirm_state,ad.scene_confirm_time, ad.scene_confirm_user,ad.is_recovery,ad.param_value,ad.param_lower,ad.param_upper,ad.last_update_time,ad.verify_time, ad.post_worker,ad.dispatcher,ad.release_time,ad.alert_cause
,ABS(ad.release_time-ad.alert_datetime) as alertDuration
,rts.system_id
FROM tb_alert_data ad
,rel_thing_system rts
,tb_thing_position ttp
AND ad.thing_code = rts.thing_code
AND ad.thing_code = ttp.thing_code
AND ad.alert_stage = #{stage}
AND ad.alert_stage != #{excluStage}
AND
#{level}
AND
#{type}
AND
#{buildingId}
AND
#{floor}
AND
#{system}
AND ad.alert_datetime >= #{startTime}
AND ad.alert_datetime <= #{endTime}
AND ad.thing_code = #{thingCode}
ORDER BY ad.alert_level is not null,ad.alert_level desc,ad.alert_datetime desc
ORDER BY ad.alert_level is not null,ad.alert_datetime DESC

SELECT ad.id,ad.thing_code,ad.metric_code,ad.alert_source,ad.alert_type,ad.alert_level,ad.alert_datetime,ad.alert_info,ad.reporter, ad.alert_stage,ad.is_repair,ad.repair_confirm_user,ad.repair_start_time,ad.repair_end_time, ad.is_manual_intervention,ad.feedback_image,ad.feedback_video,ad.scene_confirm_state,ad.scene_confirm_time, ad.scene_confirm_user,ad.is_recovery,ad.param_value,ad.param_lower,ad.param_upper,ad.last_update_time,ad.verify_time, ad.post_worker,ad.dispatcher,ad.release_time,ad.alert_cause FROM tb_alert_data ad WHERE 1=1
and ad.thing_code IN
#{thingCode}
AND ad.alert_stage in
#{alertStage}
AND ad.alert_stage != #{excluStage}
AND ad.alert_level in
#{level}
AND ad.alert_type in
#{type}
ORDER BY ad.alert_level is not null,ad.alert_level desc,ad.alert_datetime desc

SELECT
ad.id,ad.thing_code,ad.metric_code,ad.alert_source,ad.alert_type,ad.alert_level,ad.alert_datetime,ad.alert_info,ad.reporter,ad.alert_stage,ad.is_repair,ad.repair_confirm_user,ad.repair_start_time,ad.repair_end_time,ad.is_manual_intervention,ad.feedback_image,ad.feedback_video,ad.scene_confirm_state,ad.scene_confirm_time,ad.scene_confirm_user,ad.is_recovery,ad.param_value,ad.param_lower,ad.param_upper,ad.last_update_time,ad.verify_time,ad.post_worker,ad.dispatcher,ad.release_time,ad.alert_cause
,abs(ad.repair_end_time-ad.repair_start_time) as repair_duration,
tt.thing_name,tt.thing_type1_code,tt.thing_type2_code,rts.system_id

    ,ABS(ad.release_time-ad.alert_datetime) as alertDuration

FROM tb_alert_data ad,tb_thing tt,rel_thing_system rts
WHERE ad.thing_code = tt.thing_code AND ad.thing_code = rts.thing_code

    AND
    ad.alert_stage = #{stage}


    AND
    ad.alert_stage != #{excluStage}


    AND
    ad.alert_level= #{level}


    AND
    ad.alert_type = #{type}


    AND
    rts.system_id= #{system}


    AND
    ad.alert_datetime >= #{startTime} AND ad.alert_datetime < #{endTime}


    AND
    tt.thing_type1_code = #{assetType}


    AND
    tt.thing_type2_code = #{category}


    AND
    ad.thing_code = #{thingCode}



        ORDER BY alert_type, alert_level,repair_duration DESC ,repair_start_time


        ORDER BY alert_level desc,alertDuration ASC,thing_code ASC


        ORDER BY thing_code ASC, alert_info ASC


        order BY alert_info ASC,category ASC,thing_code ASC


        ORDER BY category ASC, alert_info ASC,thing_code ASC



    ORDER BY ad.alert_level desc,alertDuration ASC


    limit #{offset},#{count}

