mapper.update:
UPDATE tb_bellows_compressor_log
SET post_state = #{log.postState}, confirm_time = #{log.confirmTime}, pressure = #{log.pressure}
WHERE id = #{log.id}

mapper.select:
SELECT * FROM tb_bellows_compressor_log
WHERE operate_time >= #{startTime}
AND operate_time <= #{endTime}
ORDER BY operate_time DESC

    LIMIT #{offset},#{count}

SELECT * FROM tb_bellows_compressor_state
WHERE 1 = 1

AND thing_code IN

    #{tc}



  AND time >= #{startTime}

AND time <= #{endTime}
ORDER BY time DESC

    LIMIT #{offset},#{count}
