mapper.select:
SELECT id,duty_start_time,start_time,end_time,coal_8_thing_code,coal_13_thing_code,blending_washing_type,production_description,duration,heavy_medium_lump,heavy_medium_slack,tcs_857,tcs_858,tcs_859,reason,term
FROM tb_report_form_system_start_record
WHERE   duty_start_time<#{date}
AND term = #{term}
ORDER BY duty_start_time DESC
LIMIT 0,1

SELECT id,duty_start_time,start_time,end_time,coal_8_thing_code,coal_13_thing_code,blending_washing_type,production_description,duration,heavy_medium_lump,heavy_medium_slack,tcs_857,tcs_858,tcs_859,reason,term
FROM tb_report_form_system_start_record
WHERE   duty_start_time = #{dutyStartTime}
AND term = #{term}
ORDER BY duty_start_time ASC
