mapper.select:
select c.id as id,a.tag_name as tag_name,b.name style_name,c.editor as editor,c.create_date as create_date,c.comment as comment
from tb_metric_tag a,tb_sfmon_style b,rel_sfmon_metrictag_style c where a.tag_name like CONCAT(#{tagName},'%')
and c.style_code=b.code and  a.code=c.metric_tag_code

select c.id as id,a.tag_name as tag_name,b.name style_name,c.editor as editor,c.create_date as create_date,c.comment as comment
from tb_metric_tag a,tb_sfmon_style b,rel_sfmon_metrictag_style c where b.name like CONCAT(#{metricName},'%')
and c.style_code=b.code and  a.code=c.metric_tag_code
