mapper.select:
SELECT id, subsystem, system_name, production_line, system_type FROM tb_start_system WHERE 1 = 1
AND production_line = #{productionLine}
AND system_type = #{type}
AND system_category = #{systemCategory}

SELECT operate_id, operate_user, operate_state, operate_system, created_time, is_delete FROM tb_start_operation_record WHERE is_delete = 0
AND operate_state >#{operateState}
AND operate_state <#{finishState}

mapper.update:
UPDATE tb_start_operation_record
SET operate_state = #{operateState}
WHERE operate_id = #{operateId}

mapper.select:
SELECT ssi.device_id, ssi.production_line, ssi.start_sequence, ssi.rate_work, ssi.transformer_id, ssi.start_wait_time, ssi.start_hierarchy FROM tb_start_device_state_record AS sssdsr INNER JOIN tb_start_information AS ssi ON ssi.device_id = sssdsr.device_id AND ssi.is_delete = '0'
AND ssi.start_rule_id = #{ruleId}
WHERE 1 = 1
AND sssdsr.operate_id = #{operateId}
ORDER BY ssi.start_sequence

SELECT dataLabel FROM tb_start_signal WHERE 1 = 1
AND deviceId = #{deviceId}
AND name = #{name}

mapper.update:
