AND device_id = #{deviceId}
AND is_delete = 0

SELECT sssdr.device_id AS device_id, spl.location_serial_num AS device_code, sa.assets_name AS device_name FROM tb_start_system_device_relation AS sssdr INNER JOIN tb_start_production_location AS spl ON sssdr.device_id = spl.object_id INNER JOIN tb_start_assets AS sa ON sa.object_id = spl.assets_object_id WHERE 1 = 1
AND sssdr.system_id = #{systemId}
AND sssdr.device_id IN ( SELECT ssdsr.device_id FROM tb_start_device_state_record AS ssdsr WHERE ssdsr.operate_id = #{operateId} )

