AND sss.system_type = #{systemType}
)

SELECT rule_id, start_device_id, examine_type, examine_device_id, examine_name, operator, compare_value FROM tb_start_examine_rule WHERE 1 = 1
AND start_device_id = #{deviceId}
AND is_delete = '0'

SELECT sser.rule_id, sser.operator, sser.compare_value, sser.examine_type, sser.examine_device_id, sser.examine_name FROM tb_start_examine_rule AS sser INNER JOIN tb_start_signal AS ts ON sser.examine_device_id = ts.deviceId AND sser.examine_name = ts.`name`
AND ts.dataLabel = #{label}
WHERE 1 = 1
AND sser.rule_id = #{ruleId}
AND sser.is_delete = '0'

