mapper.select:
SELECT sser.examine_id, sser.operate_id, sser.start_device_id, sser.rule_id, sser.examine_type, sser.examine_result, sser.examine_information, sseru.examine_device_id, spl.location_serial_num AS examine_device_code, sa.assets_name AS examine_device_name FROM tb_start_examine_record AS sser INNER JOIN tb_start_examine_rule AS sseru ON sser.rule_id = sseru.rule_id INNER JOIN tb_start_production_location AS spl ON sseru.examine_device_id = spl.object_id INNER JOIN tb_start_assets AS sa ON sa.object_id = spl.assets_object_id WHERE 1 = 1
AND sser.rule_id = #{ruleId}
AND sser.operate_id = #{operateId}
AND sser.is_delete = '0'

SELECT sser.examine_id, sser.operate_id, sser.start_device_id, sser.rule_id, sser.examine_type, sser.examine_result FROM tb_start_examine_record AS sser INNER JOIN tb_start_examine_rule AS sseru ON sser.rule_id = sseru.rule_id INNER JOIN tb_start_examine_type AS sset ON sseru.examine_type = sset.id WHERE 1 = 1
AND sser.operate_id = #{operateId}
AND sser.examine_type = #{examineType}
AND sser.examine_result != 1 AND sser.is_delete = '0'

SELECT sssdr.device_id AS device_id, spl.location_serial_num AS device_code, sa.assets_name AS device_name FROM tb_start_system_device_relation AS sssdr INNER JOIN tb_start_production_location AS spl ON sssdr.device_id = spl.object_id INNER JOIN tb_start_assets AS sa ON sa.object_id = spl.assets_object_id WHERE 1 = 1
AND sssdr.system_id = #{systemId}
AND sssdr.device_id IN ( SELECT DISTINCT sser.start_device_id FROM tb_start_examine_record AS sser WHERE sser.operate_id = #{operateId} )

