ts.dataLabel
FROM
tb_start_preset_startandcoal_pararmeter AS zspsp
INNER JOIN tb_start_signal AS ts ON zspsp.dic_signal_id = ts.`name`
AND zspsp.parent_deviced_id = ts.deviceId
WHERE
zspsp.is_deleted = '0'
AND zspsp.type = #{type}
AND zspsp.dic_signal_id = #{name}
AND zspsp.start_type = #{startType}
AND zspsp.parent_deviced_id = #{deviceId};

SELECT ssi.device_id AS device_id, spl.location_serial_num AS device_code, sa.assets_name AS device_name FROM tb_start_information AS ssi INNER JOIN tb_start_production_location AS spl ON ssi.device_id = spl.object_id INNER JOIN tb_start_assets AS sa ON sa.object_id = spl.assets_object_id WHERE ssi.is_delete = '0'
AND ssi.bag_id = #{packageId}
AND ssi.device_id IN ( SELECT ssdsr.device_id FROM tb_start_device_state_record AS ssdsr WHERE ssdsr.is_delete = '0' AND ssdsr.operate_id = #{operateId} )

SELECT DISTINCT zstsd.device_id AS control_device_id, zstsd.parent_deviced_id AS start_device_id, spl.location_serial_num AS control_device_code, spl.full_name FROM tb_start_type_set_delay AS zstsd INNER JOIN tb_start_production_location AS spl ON spl.object_id = zstsd.device_id INNER JOIN tb_start_information AS ssi ON ssi.device_id = zstsd.parent_deviced_id
AND ssi.bag_id = #{packageId}
WHERE 1 = 1
AND zstsd.parent_deviced_id = #{deviceId}
