mapper.select:
SELECT operate_id, operate_user, operate_state, operate_system, created_time, system, is_delete FROM tb_stop_operation_record WHERE is_delete = 0
AND operate_state >#{operateState}
AND operate_state <#{finishState}
AND system =#{system}

SELECT sser.examine_id, sser.operate_id, sser.start_device_id, sser.rule_id, sser.examine_type, sser.examine_result, sser.examine_information, sseru.examine_device_id, spl.location_serial_num AS examine_device_code, sa.assets_name AS examine_device_name FROM tb_start_examine_record AS sser INNER JOIN tb_start_examine_rule AS sseru ON sser.rule_id = sseru.rule_id INNER JOIN tb_start_production_location AS spl ON sseru.examine_device_id = spl.object_id INNER JOIN tb_start_assets AS sa ON sa.object_id = spl.assets_object_id WHERE 1 = 1
AND sser.rule_id = #{ruleId}
AND sser.operate_id = #{operateId}
AND sser.is_delete = '0'

SELECT rule_id, stop_thing_code, examine_type, examine_thing_code, examine_name, operator, compare_value FROM tb_stop_examine_rule WHERE 1 = 1
AND stop_thing_code = #{deviceId}
AND is_delete = '0'

mapper.update:
UPDATE tb_stop_operation_record
SET is_delete = #{isDelete}
WHERE operate_id = #{operateId}

UPDATE tb_stop_manual_intervention_record
SET intervention_state = #{interventionState}

    ,relieve_person_id = #{relievePersonId}

WHERE
thing_code = #{thingCode}
AND operate_id = #{operateId}

UPDATE tb_stop_operation_record
SET operate_state = #{operateState}
WHERE operate_id = #{operateId}
